sys_script_include.script:
var NeedItUtils_Now_Validation = Class.create();
NeedItUtils_Now_Validation.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {	
	initialize: function() {
		this.x = 7;
	},	

	getDate: function(){
		gs.info("x = " + this.x);
	},

	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// If the testdate is before rightnow, return true, else return false
		if (testdate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},
    type: 'NeedItUtils_Now_Validation'
});
